import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import { EmployeeContext } from '../context/EmployeeContext';

const EmployeeDetails = () => {
  const { id } = useParams();
  const { addToFavorites, favorites } = useContext(EmployeeContext);
  const [employee, setEmployee] = useState(null);

  useEffect(() => {
    const fetchEmployee = async () => {
      try {
        const response = await fetch(`https://randomuser.me/api/?uuid=${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch employee');
        }
        const data = await response.json();
        setEmployee(data.results[0]);
      } catch (error) {
        console.error('Error fetching employee:', error);
      }
    };

    fetchEmployee();
  }, [id]);

  const isFavorite = favorites.some(fav => fav.email === employee?.email);

  const handleAddToFavorites = () => {
    addToFavorites(employee);
  };

  if (!employee) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Employee Details</h2>
      <p>Name: {`${employee.name.first} ${employee.name.last}`}</p>
      <p>Email: {employee.email}</p>
      <p>Phone: {employee.phone}</p>
      <button onClick={handleAddToFavorites} disabled={isFavorite}>Add to Favorites</button>
    </div>
  );
};

export default EmployeeDetails;
