// EmployeeDetails.js

import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { EmployeeContext } from '../context/EmployeeContext';

const EmployeeDetails = () => {
  const { id } = useParams(); // Retrieve the 'id' parameter from the URL
  const { favorites } = useContext(EmployeeContext);
  const [employee, setEmployee] = useState(null);

  useEffect(() => {
    const storedFavorites = JSON.parse(localStorage.getItem('FAVS')) || [];
    const foundEmployee = storedFavorites.find(emp => emp.login.uuid === id);

    if (foundEmployee) {
      setEmployee(foundEmployee);
    }
  }, [id, favorites]);

  if (!employee) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Employee Details</h2>
      <p>Name: {`${employee.name.first} ${employee.name.last}`}</p>
      <p>Email: {employee.email}</p>
      <p>Phone: {employee.phone}</p>
      {/* Display other details as needed */}
    </div>
  );
};

export default EmployeeDetails;
