import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const EmployeeDetails = () => {
  const { id } = useParams();
  const [employee, setEmployee] = useState(null);

  useEffect(() => {
    const fetchEmployee = async () => {
      try {
        const response = await fetch(`https://randomuser.me/api/?login.uuid=${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch employee details');
        }
        const data = await response.json();
        setEmployee(data.results[0]);
      } catch (error) {
        console.error('Error fetching employee details:', error);
      }
    };

    fetchEmployee();
  }, [id]);

  if (!employee) {
    return <div>Loading employee details...</div>;
  }

  return (
    <div>
      <h2>{`${employee.name.first} ${employee.name.last}`}</h2>
      <p>Email: {employee.email}</p>
      <p>Phone: {employee.phone}</p>
      <p>Address: {`${employee.location.street.number} ${employee.location.street.name}, ${employee.location.city}, ${employee.location.country}`}</p>
      {/* Add map component here if using Leaflet for showing location */}
    </div>
  );
};

export default EmployeeDetails;
