import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { EmployeeContext } from '../context/EmployeeContext';
import './EmployeeItem.css'; // Assuming you will create this CSS file

const EmployeeItem = ({ employee }) => {
  const { addToFavorites } = useContext(EmployeeContext);

  return (
    <div className="employee-card">
      <img src={employee.picture.thumbnail} className="employee-image" alt={`${employee.name.first} ${employee.name.last}`} />
      <div className="employee-details">
        <h5>{`${employee.name.first} ${employee.name.last}`}</h5>
        <p>Age: {employee.dob.age}</p>
        <p>Location: {employee.location.city}, {employee.location.country}</p>
        <p>{employee.email}</p>
        <Link to={`/employee/${employee.login.uuid}`} className="details-link">More Details</Link>
        <button className="add-favorite-button" onClick={() => addToFavorites(employee)}>Add to Favorites</button>
      </div>
    </div>
  );
};

export default EmployeeItem;
