import React, { useState } from 'react';

function EmployeeList({ company }) {
  const [searchResults, setSearchResults] = useState([]);

  // Function to fetch data from API based on company name
  const fetchEmployees = async () => {
    try {
      const response = await fetch(`https://randomuser.me/api/?results=10&seed=${company}`);
      if (!response.ok) {
        throw new Error('Failed to fetch employees');
      }
      const data = await response.json();
      setSearchResults(data.results); // Assuming data.results contains the array of employees
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  // Fetch data when component mounts or when 'company' changes
  useEffect(() => {
    fetchEmployees();
  }, [company]);

  return (
    <div>
      <h2>Search Results</h2>
      <ul>
        {searchResults.map((employee, index) => (
          <li key={index}>
            <div>Name: {employee.name.first} {employee.name.last}</div>
            <div>Age: {employee.dob.age}</div>
            <div>Location: {employee.location.city}, {employee.location.country}</div>
            <img src={employee.picture.thumbnail} alt="Profile" />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default EmployeeList;
